          <h2>Generation: #</h2>
          <div className="grid-wrap">
            <div className="grid">
              <div style={{}}>
                {grid.map((row, i)=> {
                  return <Row row={row} key={i}/>
                })}
              </div>
            </div>
            <div className="presets">
              {presets.map((preset, i) => {
                return <Preset preset={preset} key={i} setSelectedPreset={setSelectedPreset} selectedPreset={selectedPreset}/>
