import React, { useEffect, useState, useRef } from 'react';
import Preset from './Preset';
import Grid from './Grid';
import produce from 'immer';
import { preset1, preset2, preset3, randomPreset, countNeighbors, wrapCellsAround } from '../utils/helper';

const Main = () => {
  const [grid, setGrid] = useState([]);
  const [selectedPreset, setSelectedPreset] = useState('');
  const presets = [{name: 'Preset 1', func: preset1}, 
                   {name: 'Preset 2', func: preset2},
                   {name: 'Preset 3', func: preset3},
                   {name: 'Random', func: randomPreset}]
  const [reload, setReload] = useState(false);
  const [disable, setDisable] = useState(true);

  const [numberOfCells, setNumberOfCells] = useState(0);
  const numberOfCellsRef = useRef(numberOfCells);
  numberOfCellsRef.current = numberOfCells;

  const [simulate, setSimulate] = useState(false);
  const simulateRef = useRef(simulate);
  simulateRef.current = simulate;

  useEffect(() => {

    if (!simulateRef.current) {
      let arr = new Array(25);

      for(let i = 0; i < arr.length; i++) {
        arr[i] = new Array(25)
      }

      for (let i = 0; i < arr.length; i++) {
        for (let j = 0; j < arr[i].length; j++) {
          arr[i][j] = {alive: false, coor: [i,j]}
        }
      }
      setGrid(arr)

    } 

  }, [reload])


  const startSimulation = () => {

    if (!simulateRef.current) {
      return 
    }

    //check if the grid is not empty
    if (numberOfCells > 0) {

      setGrid(grid => {
        return produce(grid, gridCopy => {

          // make cells wrap around the grid
          const coordinates = {};
          wrapCellsAround(grid, coordinates);


          for (let i = 0; i < grid.length; i++) {

            for (let j = 0; j < grid[i].length; j++) {

              if (grid[i][j].alive === true) {
                countNeighbors(grid, gridCopy, coordinates, i, j, true)
              }
              if (grid[i][j].alive === false) {
                countNeighbors(grid, gridCopy, coordinates, i, j, false)

              }
            }
          }
        });

      });

      setTimeout(startSimulation, 1000)
    }

  }


  const pauseSimulation = () => {
    setSimulate(false)
  }

  const clearSimulation = () => {
    setSimulate(false);
    setReload(!reload);
    setSelectedPreset('');
  }


  return (
    <div className="main-wrap">

      <h1>Conway's Game of Life</h1>

      <div className="boxes-wrap">

        <div className="left-box">
          <h2>Generation: #</h2>
          <div className="grid-wrap">

            <Grid grid={grid} setGrid={setGrid} 
                  setDisable={setDisable} 
                  numberOfCells={numberOfCells} 
                  setNumberOfCells={setNumberOfCells} 
                  numberOfCellsRef={numberOfCellsRef}
                  />

            <div className="presets">
              {presets.map((preset, i) => {

                return <Preset preset={preset} key={i} 
                               setSelectedPreset={setSelectedPreset} 
                               selectedPreset={selectedPreset}
                               setGrid={setGrid}
                               />
              })}
            </div>
          </div>
          <div className="buttons">
            <button onClick={() => {
                      setSimulate(!simulate);
                      simulateRef.current = true;
                      startSimulation();
                      }}
                    disabled={simulate || disable || numberOfCells === 0 ? true : false}
            >Play
            </button>
            <button onClick={pauseSimulation}>Pause</button>
            <button onClick={clearSimulation}>Clear</button>
          </div>
        </div>

        <div className="right-box">
          <h2>Rules:</h2>
          <ul>
            <li>item</li>
            <li>item</li>
            <li>item</li>
          </ul>
        </div>

      </div>

    </div>
  )
}

export default Main
