import React from 'react';
import produce from 'immer';

const Cell = ( { cell, setGrid }) => {

  return (
    <div onClick={() => {
      setGrid(grid => {
        return produce(grid, gridCopy => {
          gridCopy[cell.coor[0]][cell.coor[1]].alive = grid[cell.coor[0]][cell.coor[1]].alive ? 
                   false : true
        })
      })

    }} style={{border: `${cell.alive ? '1px solid gray' : '1px solid transparent'}`, height: '15px', width: '100%', 
                 backgroundColor: 
                 `${cell.alive && cell.age < 10 ? '#ece7e7' : cell.alive && cell.age < 30 ? '#00FF00' : cell.alive && cell.age < 50 ? '#FFFF00' : cell.alive && cell.age < 100 ? '#CC00FF' : cell.alive && cell.age > 100 ? '#0033FF' : '#6E0DD0'}`}}>  


    </div>
  )
}

export default Cell
