    for (let i = 0; i < buffer.length; i++) {
      if (i === 0) {
        let a = [grid[grid.length - 1][grid.length - 1]].concat(grid[grid.length - 1]);
        a.push(grid[grid.length - 1][0]);
        buffer[i] = a;
      } else if (i === grid.length - 1) {
        let a = [grid[0][grid.length - 1]].concat(grid[0]);
        a.push(grid[0][0]);
        buffer[i] = a;
      } else {
        buffer[i] = new Array(27);
      }
    }

    const build_left_edge = () => {
      let index = 1;

      while(index < grid.length - 1) {
        buffer[index][0] = grid[index][grid.length - 1];
        index ++;
      }

    }

    const build_right_edge = () => {
      let index = 1;

      while(index < grid.length - 1) {
