import React from 'react';
import produce from 'immer';

const Cell = ( { cell, setGrid, setDisable, numberOfCells, setNumberOfCells }) => {

  return (
    <div onClick={() => {
      setGrid(grid => {
        return produce(grid, gridCopy => {
          gridCopy[cell.coor[0]][cell.coor[1]].alive = grid[cell.coor[0]][cell.coor[1]].alive ? false : true
        })
      })

      if (cell.alive === true) {
        setNumberOfCells(numberOfCells += 1);
      } else {
        setNumberOfCells(numberOfCells -= 1);
      }

      setDisable(false);
    }} style={{border: '1px solid gray', height: '15px', width: '15px', 
                 backgroundColor: `${cell.alive ? 'black' : 'white'}`}}>    
    </div>
  )
}

export default Cell
