import React from 'react';
import produce from 'immer';

const Cell = ( { cell, setGrid, setDisable, numberOfCells, setNumberOfCells, numberOfCellsRef }) => {

  return (
    <div onClick={() => {
      setGrid(grid => {
        return produce(grid, gridCopy => {
          if (gridCopy[cell.coor[0]][cell.coor[1]].alive) {
            let num = numberOfCells + 1;
            setNumberOfCells(num);
            numberOfCellsRef.current = num;
          } else if (gridCopy[cell.coor[0]][cell.coor[1]].alive === false) {
            let num = numberOfCells - 1;
            setNumberOfCells(num);
            numberOfCellsRef.current = num;
          }
          gridCopy[cell.coor[0]][cell.coor[1]].alive = grid[cell.coor[0]][cell.coor[1]].alive ? 
                   false : true

          // setNumberOfCells(num => {
          //   return produce(num, numCopy => {

          //   })
          // })
        })
      })

      // if (cell.alive === true) {
      //   let num = numberOfCells + 1;
      //   setNumberOfCells(num);
      //   numberOfCellsRef.current = num;
      // } else {
      //   let num = numberOfCells - 1;
      //   setNumberOfCells(num);
      //   numberOfCellsRef.current = num;
      // }

      setDisable(false);
    }} style={{border: '1px solid gray', height: '15px', width: '15px', 
                 backgroundColor: `${cell.alive ? 'black' : 'white'}`}}>    
    </div>
  )
}

export default Cell
